import React,{useState,useEffect} from 'react'
import {Form,FormControl} from "react-bootstrap";
import Axios from "axios";
import "./profile.css";
import {useHistory} from "react-router-dom";


function Profile() {


 const [lista, setlist] = useState([]);
 const [search, setsearch] = useState("");
 const [send_firstname, setsend_firstname] = useState("");
 const [send_lastname, setsend_lastname] = useState("");
 const [picture, setpicture] = useState("");
 const [title, settitle] = useState("");
 const history = useHistory();

 useEffect(()=>{
    Axios.get('https://tutorial4-api.herokuapp.com/api/users/').then((response)=>{
        console.log(response.data.data);
         setlist(response.data.data);
      });

 },[]);

  const changePage = (id) => {
      console.log('https://tutorial4-api.herokuapp.com/api/users/'+id);
    Axios.get('https://tutorial4-api.herokuapp.com/api/users/'+id,{'user id':id}).then((response)=>{
        console.log(response.data.data.firs);
        setsend_firstname(response.data.data.firstName);
        // setsend_lastname(response.data.data.lastName);
        // setpicture(response.data.data.picture);
        // settitle(response.data.data.title);

      });
      alert(send_firstname);
      history.push("/Profiledetails",{Firstname: send_firstname, Lastname: send_lastname, Picture: picture, Title: title}
      );
  }  

    return (
        <div className="Profile">
            <Form >

            <div style={{'maxWidth':'fit-content','marginBottom':'49px',padding: '0px 164px'}}>
                        <Form.Label className="Login-Header">Profile Page</Form.Label>
                        <FormControl type="search" placeholder="Search" className="mr-2" aria-label="Search" onChange={(event) => setsearch(event.target.value)}/>
                        </div>
                        <div>
                            {lista.map((val,key)=>{
                                if(search===""){
                                return( 
                                            <div className="container-wrapper-profile" id={val.id} onClick={() => changePage(`${val.id}`)}>
                                                <div>
                                                     <img src={val.picture}/>
                                                </div>
                                                <div style={{padding:'20px'}}>
                                                    <div><Form.Label>Firstname : </Form.Label><Form.Label>{val.firstName}</Form.Label></div>
                                                    <div><Form.Label>Lastname : </Form.Label><Form.Label>{val.lastName}</Form.Label></div>
                                                    <div><Form.Label>Email : </Form.Label><Form.Label>{val.email}</Form.Label></div>
                                                </div>
                                             </div>
                                      )
                                 }
                                 else{

                                     if(search === val.firstName || search === val.lastName){

                                        return( 
                                            <div className="container-wrapper-profile" id={val.id} onClick={() => changePage(`${val.id}`)}>
                                                <div>
                                                     <img src={val.picture}/>
                                                </div>
                                                <div style={{padding:'20px'}}>
                                                    <div><Form.Label>Firstname : </Form.Label><Form.Label>{val.firstName}</Form.Label></div>
                                                    <div><Form.Label>Lastname : </Form.Label><Form.Label>{val.lastName}</Form.Label></div>
                                                    <div><Form.Label>Email : </Form.Label><Form.Label>{val.email}</Form.Label></div>
                                                </div>
                                             </div>
                                      )
                                     }
                                 }
                            })}
                            </div>       
            </Form>
        </div>
    )
}

export default Profile
