import React, { useState } from "react";
import {Button,Form} from "react-bootstrap";
import "./Login.css";
import Axios from "axios";

 function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  function validateForm() {
    return email.length > 0 && password.length > 0;
  }

  function handleSubmit(event) {
    event.preventDefault();
  }

 const submit = () => {
    Axios.post('https://tutorial4-api.herokuapp.com/api/users/login',{email:email,password:password}).then((response)=>{
        console.log(response);
      });
  }


  return (
    <div className="Login">
            <Form className="container-wrapper" onSubmit={handleSubmit}>
                        <Form.Label className="Login-Header">Login</Form.Label>
                    <Form.Group size="lg" controlId="email">
                    <Form.Label>Email</Form.Label>
                    <Form.Control
                        autoFocus
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    </Form.Group>
                    <Form.Group size="lg" controlId="password">
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    </Form.Group>
                    <Button block size="lg"  onClick={submit}>
                    Login
                    </Button>
            </Form>
    </div>
  );
}
export default Login;
