import React, { useState } from "react";
import {Button,Form} from "react-bootstrap";
import "./Login.css";
import Axios from "axios";
import {useHistory} from "react-router-dom";

 function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const history = useHistory();

  function validateForm() {
    return email.length > 0 && password.length > 0;
  }

  function handleSubmit(event) {
    event.preventDefault();
  }

 const submit = () => {
    Axios.post('https://tutorial4-api.herokuapp.com/api/users/login',{email:"jonsnow@westeros.com",password:"G@me0fthr0ne5"}).then((response)=>{
        console.log(response);
        console.log(response.data.data.message);
        if(response.data.data.message==="Login Successful")
           history.push("/Profile");
      });

  }

  const getData = () => {
    Axios.get('https://tutorial4-api.herokuapp.com/api/users/').then((response)=>{
        console.log(response);
      });
  }


  return (
    <div className="Login">
            <Form className="container-wrapper" onSubmit={handleSubmit}>
                        <Form.Label className="Login-Header">Login</Form.Label>
                    <Form.Group size="lg" controlId="email">
                    <Form.Label>Email</Form.Label>
                    <Form.Control
                        autoFocus
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    </Form.Group>
                    <Form.Group size="lg" controlId="password">
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    </Form.Group>
                    <Button block size="lg"  onClick={submit}>
                    Login
                    </Button>
                    {/* <Link to="/Profile" className="btn btn-primary" onClick={submit}>Sign up</Link> */}
            </Form>
    </div>
  );
}
export default Login;
